// Parse incoming note from test webhook
const body = $input.item.json.body || $input.item.json;
const query = $input.item.json.query || {};

// Support multiple input formats
const title = body.title || query.title || 'Untitled Test Note';
const content = body.content || query.content || body.text || '';
const timestamp = body.created_at || body.timestamp || query.timestamp || new Date().toISOString();
const sourceType = body.source_type || query.source_type || 'test_webhook';
const testRun = body.test_run || query.test_run;

// TEST ENVIRONMENT: test_run is REQUIRED
if (!testRun || testRun.trim().length === 0) {
  throw new Error('test_run field is required for test environment. Example: {"test_run": "feature_v1"}');
}

// Validate required fields
if (!content || content.trim().length === 0) {
  throw new Error('Content is required and cannot be empty');
}

// Generate content hash for duplicate detection
function simpleHash(str) {
  let hash = 2166136261;
  for (let i = 0; i < str.length; i++) {
    hash ^= str.charCodeAt(i);
    hash += (hash << 1) + (hash << 4) + (hash << 7) + (hash << 8) + (hash << 24);
  }
  return (hash >>> 0).toString(16);
}

const contentHash = simpleHash(content.trim());

// Calculate metadata
const words = content.trim().split(/\s+/).filter(w => w.length > 0);
const wordCount = words.length;
const characterCount = content.length;

// Extract tags if present (format: #tag)
const tagMatches = content.match(/#(\w+)/g) || [];
const tags = tagMatches.map(t => t.substring(1));

return {
  json: {
    title: title.trim(),
    content: content.trim(),
    contentHash: contentHash,
    timestamp: timestamp,
    wordCount: wordCount,
    characterCount: characterCount,
    tags: tags,
    sourceType: sourceType,
    testRun: testRun,
    status: 'pending'
  }
};

const Database = require('better-sqlite3');

const noteData = $json;

try {
  const db = new Database('/selene/data-test/selene-test.db', { readonly: true });

  const query = `SELECT id, title, created_at FROM raw_notes WHERE content_hash = ? LIMIT 1`;
  const stmt = db.prepare(query);
  const result = stmt.get(noteData.contentHash);

  db.close();

  return { json: result || { id: null } };

} catch (error) {
  console.error('SQLite Duplicate Check Error:', error);
  return { json: { id: null } };
}

const Database = require('better-sqlite3');

const noteData = $('Parse Test Note Data').item.json;

try {
  const db = new Database('/selene/data-test/selene-test.db');

  // Insert into TEST database
  const insertQuery = `INSERT INTO raw_notes (
    title, content, content_hash, source_type,
    word_count, character_count, tags,
    created_at, imported_at, status, status_apple, test_run
  ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, datetime('now'), 'pending', 'pending_apple', ?)`;

  const stmt = db.prepare(insertQuery);
  const result = stmt.run(
    noteData.title,
    noteData.content,
    noteData.contentHash,
    noteData.sourceType,
    noteData.wordCount,
    noteData.characterCount,
    JSON.stringify(noteData.tags),
    noteData.timestamp,
    noteData.testRun
  );

  db.close();

  return {
    json: {
      note_id: result.lastInsertRowid,
      title: noteData.title,
      wordCount: noteData.wordCount,
      contentHash: noteData.contentHash,
      sourceType: noteData.sourceType,
      testRun: noteData.testRun
    }
  };

} catch (error) {
  console.error('SQLite Insert Error:', error);
  throw new Error(`Failed to insert test note: ${error.message}`);
}

const insertResult = $json;

return {
  json: {
    success: true,
    action: 'stored_in_test',
    message: '[TEST] Note successfully ingested into test database',
    noteId: insertResult.note_id,
    title: insertResult.title,
    wordCount: insertResult.wordCount,
    contentHash: insertResult.contentHash,
    sourceType: insertResult.sourceType,
    testRun: insertResult.testRun,
    status: 'pending'
  }
};

const ingestionData = $('Build Success Response').item.json;
const processingData = $json;

return {
  json: {
    success: true,
    action: 'stored_and_processed_test',
    message: '[TEST] Note ingested and LLM processing triggered in test environment',
    noteId: ingestionData.noteId,
    title: ingestionData.title,
    wordCount: ingestionData.wordCount,
    testRun: ingestionData.testRun,
    processing: {
      status: processingData.success ? 'processing' : 'queued',
      conceptsExtracted: processingData.conceptsExtracted || 0,
      primaryTheme: processingData.primaryTheme || null,
      sentiment: processingData.sentiment || null
    }
  }
};

const noteData = $('Parse Test Note Data').item.json;
const existingNote = $('Check for Duplicate').item.json;

return {
  json: {
    success: true,
    action: 'duplicate_skipped',
    message: '[TEST] Duplicate note detected and skipped',
    contentHash: noteData.contentHash,
    title: noteData.title,
    testRun: noteData.testRun,
    existingNoteId: existingNote.id,
    existingNoteTitle: existingNote.title,
    existingNoteCreatedAt: existingNote.created_at
  }
};
